def move_character(direction: str, position: int) -> int:
    if direction == "left":
        position -= 5
    elif direction == "right":
        position += 5

    if position < 1:
        position = 1
    elif position > 512:
        position = 512

    return position

def update_score(score: int) -> int:
    if score % 3 == 0:
        score += 10

    if score % 2 == 0:
        score *= 2

    return score

def check_age(age: int) -> None:
    if age < 13:
        print("Child")
    elif 13 <= age <= 19:
        print("Teen")
    else:
        print("Adult")

def calculate_power(base: int, exponent: int) -> int:
    result = 1
    for i in range(exponent):
        result *= base
    return result

